import React from "react";
import Card from "./Card";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import AccountBalanceWalletSharpIcon from "@mui/icons-material/AccountBalanceWalletSharp";
import ProfileButton from "./ProfileButton";
import SettingsSharpIcon from "@mui/icons-material/SettingsSharp";
import IosShareSharpIcon from "@mui/icons-material/IosShareSharp";
import LogoutSharpIcon from "@mui/icons-material/LogoutSharp";

export default function ProfileCards() {
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate("/login"); // Redirect to the login page after logout
  };

  return (
    <div>
      <Card color="#064495">
        <div
          style={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "space-around",
            alignItems: "center",
          }}
        >
          <AccountCircleIcon fontSize="large" />

          <div style={{ display: "flex", flexDirection: "column" }}>
            <span
              style={{
                color: "white",
                fontSize: "13px",
                marginBottom: "1em",
                color: "#d1cfc9",
              }}
            >
              Username
            </span>
            <span
              style={{ fontWeight: "600", color: "white", fontSize: "25px" }}
            >
              John Doe
            </span>
          </div>
        </div>
      </Card>

      <ProfileButton Icon={AccountBalanceWalletSharpIcon} content="Account" />
      <ProfileButton Icon={SettingsSharpIcon} content="Settings" />
      <ProfileButton Icon={IosShareSharpIcon} content="Export Data" />
      <ProfileButton Icon={LogoutSharpIcon} content="Logout" />
    </div>
  );
}
