import React, { useState } from 'react';
import BalanceCard from "../../components/BalanceCard";
import MonthlyBudgetCard from "../../components/MonthlyBudgetCard";
import SavingGoalCard from "../../components/SavingGoalCard";
import "./styles.css";
import CashCard from "../../components/CashCard";
import BottomBar from "../../components/BottomBar";
import AddIncomeForm from "../../components/AddIncomeForm";
import AddExpenseForm from "../../components/AddExpenseForm";

export default function Dashboard({ balance, month, goal, income, expenses }) {
  const [showAddIncomeForm, setShowAddIncomeForm] = useState(false);
  const [showAddExpenseForm, setShowAddExpenseForm] = useState(false);

  const handleAddIncomeClick = () => {
    setShowAddIncomeForm(true);
  };

  const handleAddExpenseClick = () => {
    setShowAddExpenseForm(true);
  };

  const handleAddIncome = (incomeData) => {
    // Logic to handle adding income
    console.log('Add Income:', incomeData);
    setShowAddIncomeForm(false);
  };

  const handleAddExpense = (expenseData) => {
    // Logic to handle adding expense
    console.log('Add Expense:', expenseData);
    setShowAddExpenseForm(false);
  };

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-header">
        <h3 style={{ margin: 0 }}>Dashboard</h3>
      </div>
      <hr />
      <div className="dashboard-card-container">
        <BalanceCard balance={balance} />
        <MonthlyBudgetCard balance={balance} month={month} />
        <SavingGoalCard goal={goal} balance={balance} />
        <CashCard income={income} expenses={expenses} />
      </div>
      <div className="bottomBar">
        <BottomBar
          onAddIncomeClick={handleAddIncomeClick}
          onAddExpenseClick={handleAddExpenseClick}
        />
      </div>
      {showAddIncomeForm && (
        <div className="transaction-form">
          <h4>Add Income</h4>
          <AddIncomeForm onAddIncome={handleAddIncome} />
        </div>
      )}
      {showAddExpenseForm && (
        <div className="transaction-form">
          <h4>Add Expense</h4>
          <AddExpenseForm onAddExpense={handleAddExpense} />
        </div>
      )}
    </div>
  );
}
