import React from 'react';
import { useNavigate } from 'react-router-dom';
import BalanceCard from '../../components/BalanceCard';
import MonthlyBudgetCard from '../../components/MonthlyBudgetCard';
import SanvingGoalCard from '../../components/SavingGoalCard';
import CashCard from '../../components/CashCard';
import BottomBar from '../../components/BottomBar';

export default function Dashboard({ incomes, expenses }) {
  const navigate = useNavigate();

  const handleAddButtonClick = () => {
    navigate('/add');
  };

  // Calculate the sum of incomes
  const totalIncome = incomes.reduce((sum, income) => sum + parseFloat(income.value), 0);

  // Calculate the sum of expenses
  const totalExpense = expenses.reduce((sum, expense) => sum + parseFloat(expense.value), 0);

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-header">
        <h3 style={{ margin: 0 }}>Dashboard</h3>
      </div>
      <hr />
      <div className="dashboard-card-container">
        <BalanceCard balance={totalIncome - totalExpense} />
        <MonthlyBudgetCard balance={totalIncome - totalExpense} month="January" />
        <SanvingGoalCard goal="15000" balance={totalIncome - totalExpense} />
        <CashCard income={totalIncome} expenses={totalExpense} />
      </div>
      <div className="bottomBar">
        <BottomBar onAddButtonClick={handleAddButtonClick} />
      </div>
    </div>
  );
}
