import React, { useState } from 'react';
import AddIncomeForm from '../../components/AddIncomeForm';
import AddExpenseForm from '../../components/AddExpenseForm';
import BottomBar from '../../components/BottomBar';
import Card from '../../components/Card';

export default function Add() {
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [budget, setBudget] = useState(0);

  const handleAddIncome = (incomeData) => {
    // Update the income value
    setIncome(income + incomeData.value);
  };

  const handleAddExpense = (expenseData) => {
    // Update the expense value
    setExpense(expense + expenseData.value);
  };

  const handleSetBudget = (event) => {
    const value = parseInt(event.target.value, 10);
    setBudget(value);
  };

  const availableMoney = income - expense;

  return (
    <div>
      <div 
        style={{
          display: 'flex',
          flexDirection: 'rows',
          justifyContent: 'space-around',
          marginTop: '10px',
        }}
      >
        <Card color='white' width='40%'>
          <h3>Add Income</h3>
          <AddIncomeForm onAddIncome={handleAddIncome} />
        </Card>
        <Card color='white' width='40%'>
          <h3>Add Expense</h3>
          <AddExpenseForm onAddExpense={handleAddExpense} />
        </Card>
      </div>

      <div 
        style={{
          display: 'flex',
          flexDirection: 'rows',
          justifyContent: 'space-around',
          marginTop: '10px',
        }}
      >
        <Card color='white' width='40%'>
          <h3>Add Saving Goal</h3>
        </Card>
        <Card color='white' width='40%'>
          <h3>Change Budget</h3>
          <input type="number" value={budget} onChange={handleSetBudget} />
        </Card>
      </div>

      <div>
        <h3>Available Money: {availableMoney}</h3>
      </div>

      <div className="bottomBar">
        <BottomBar />
      </div>
    </div>
  );
}
