import React, { useState } from 'react';

const AddExpenseForm = ({ onAddExpense }) => {
  const [expenseName, setExpenseName] = useState('');
  const [expenseValue, setExpenseValue] = useState('');
  const [expenseFrequency, setExpenseFrequency] = useState('monthly'); // Set default frequency to monthly

  const handleExpenseNameChange = (e) => {
    setExpenseName(e.target.value);
  };

  const handleExpenseValueChange = (e) => {
    setExpenseValue(e.target.value);
  };

  const handleExpenseFrequencyChange = (e) => {
    setExpenseFrequency(e.target.value);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const newExpense = {
      name: expenseName,
      value: expenseValue,
      frequency: expenseFrequency,
    };

    onAddExpense(newExpense);
    setExpenseName('');
    setExpenseValue('');
    setExpenseFrequency('monthly');
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        Expense Name:
        <input type="text" value={expenseName} onChange={handleExpenseNameChange} />
      </label>
      <br />
      <label>
        Expense Value:
        <input type="text" value={expenseValue} onChange={handleExpenseValueChange} />
      </label>
      <br />
      <label>
        Frequency:
        <select value={expenseFrequency} onChange={handleExpenseFrequencyChange}>
          <option value="monthly">Monthly</option>
          <option value="annual">Annual</option>
        </select>
      </label>
      <br />
      <button type="submit">Add Expense</button>
    </form>
  );
};

export default AddExpenseForm;
