import React, { useState } from 'react';
import BalanceCard from "../../components/BalanceCard";
import MonthlyBudgetCard from "../../components/MonthlyBudgetCard";
import SanvingGoalCard from "../../components/SavingGoalCard";
import CashCard from "../../components/CashCard";
import BottomBar from "../../components/BottomBar";
import AddTransactionForm from "../../components/AddTransactionForm";

export default function Dashboard({ balance, month, goal }) {
  const [showAddTransactionForm, setShowAddTransactionForm] = useState(false);
  const [income, setIncome] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const handleAddTransactionClick = () => {
    setShowAddTransactionForm(true);
  };

  const handleAddTransaction = (transaction) => {
    if (transaction.type === 'income') {
      setIncome([...income, transaction]);
    } else if (transaction.type === 'expense') {
      setExpenses([...expenses, transaction]);
    }
    setShowAddTransactionForm(false);
  };

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-header">
        <h3 style={{ margin: 0 }}>Dashboard</h3>
      </div>
      <hr />
      <div className="dashboard-card-container">
        <BalanceCard balance={balance}></BalanceCard>
        <MonthlyBudgetCard balance={balance} month={month}></MonthlyBudgetCard>
        <SanvingGoalCard goal={goal} balance={balance} />
        <CashCard
          income={income.reduce((total, item) => total + item.value, 0)}
          expenses={expenses.reduce((total, item) => total + item.value, 0)}
        />
      </div>
      <div className="bottomBar">
        <BottomBar onAddTransactionClick={handleAddTransactionClick} />
      </div>
      {showAddTransactionForm && (
        <div className="transaction-form">
          <h4>Add Transaction</h4>
          <AddTransactionForm onAddTransaction={handleAddTransaction} />
        </div>
      )}
    </div>
  );
}
