import React, { useState } from 'react';
import AddIncomeForm from '../../components/AddIncomeForm';
import AddExpenseForm from '../../components/AddExpenseForm';
import BottomBar from '../../components/BottomBar';
import Card from '../../components/Card';

export default function Add() {
  const [income, setIncome] = useState(0);
  const [expense, setExpense] = useState(0);
  const [budget, setBudget] = useState(0);

  const handleAddIncome = (incomeData) => {
    // Handle adding income
    const newIncome = parseFloat(incomeData.value);
    setIncome(income + newIncome);
  };

  const handleAddExpense = (expenseData) => {
    // Handle adding expense
    const newExpense = parseFloat(expenseData.value);
    setExpense(expense + newExpense);
  };

  const handleSetBudget = (budgetData) => {
    // Handle setting budget
    const newBudget = parseFloat(budgetData.value);
    setBudget(newBudget);
  };

  const availableMoney = income - expense;

  return (
    <div>
      <div
        style={{
          display: 'flex',
          flexDirection: 'rows',
          justifyContent: 'space-around',
          marginTop: '10px',
        }}
      >
        <Card color="white" width="40%">
          <h3>Add Income</h3>
          <AddIncomeForm onAddIncome={handleAddIncome} />
        </Card>
        <Card color="white" width="40%">
          <h3>Add Expense</h3>
          <AddExpenseForm onAddExpense={handleAddExpense} />
        </Card>
      </div>

      <div
        style={{
          display: 'flex',
          flexDirection: 'rows',
          justifyContent: 'space-around',
          marginTop: '10px',
        }}
      >
        <Card color="white" width="40%">
          <h3>Add Saving Goal</h3>
        </Card>
        <Card color="white" width="40%">
          <h3>Change Budget</h3>
          <input type="number" value={budget} onChange={(e) => setBudget(e.target.value)} />
        </Card>
      </div>

      <div>
        <h3>Available Money: {availableMoney}</h3>
      </div>

      <div className="bottomBar">
        <BottomBar />
      </div>
    </div>
  );
}
