import React, { useState } from 'react';
import AddExpenseForm from '../../components/AddExpenseForm';
import AddIncomeForm from '../../components/AddIncomeForm';

const AddPage = () => {
  const [selectedTab, setSelectedTab] = useState('expense');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  const handleAddExpense = (expenseData) => {
    // Logic to handle adding expense
    console.log('Add Expense:', expenseData);
  };

  const handleAddIncome = (incomeData) => {
    // Logic to handle adding income
    console.log('Add Income:', incomeData);
  };

  return (
    <div>
      <h1>Add Page</h1>
      <div>
        <button onClick={() => handleTabChange('expense')}>Add Expense</button>
        <button onClick={() => handleTabChange('income')}>Add Income</button>
      </div>
      {selectedTab === 'expense' && <AddExpenseForm />}
      {selectedTab === 'income' && <AddIncomeForm onAddIncome={handleAddIncome} />}
    </div>
  );
};

export default AddPage;
