import React, { useState } from 'react';
import ExpenseForm from '../../components/AddExpenseForm';
import IncomeForm from '../../components/IncomeForm';

const AddPage = () => {
  const [selectedTab, setSelectedTab] = useState('expense');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <div>
      <h1>Add Page</h1>
      <div>
        <button onClick={() => handleTabChange('expense')}>Add Expense</button>
        <button onClick={() => handleTabChange('income')}>Add Income</button>
      </div>
      {selectedTab === 'expense' && <ExpenseForm />}
      {selectedTab === 'income' && <IncomeForm />}
    </div>
  );
};

export default AddPage;
